 $message.rule_group1 == "windows" AND $message.rule_group3 == "sysmon_event7" AND ! has_field("data_win_eventdata_company")
then
 let ldata = lookup_value(
 lookup_table: "software-vendors",
 key: to_string($message.data_win_eventdata_signature)
 );
 set_field("software_approved", ldata);
 let build_message_0 = to_string(" Sysmon - Event 7: Image Loaded With Empty Company Name - ");
 let build_message_1 = concat(build_message_0, to_string($message.data_win_eventdata_signature));
 set_field("rule_description", build_message_1);
 set_field("syslog_level", "WARNING");
end

