rule "WINDOWS SYSMON EVENT 15 - SOCFORTRESS THREAT INTEL"
when
 $message.rule_group1 == "windows" AND $message.rule_group3 == "sysmon_event_15"
then
 let sha256_and_ampersand = concat(to_string($message.sha256), "&customer_code=");
 let new_key = concat(sha256_and_ampersand, to_string($message.agent_labels_customer));
 let ldata = lookup(
 lookup_table: "socfortress_threat_intel",
 key: new_key
 );
 set_fields(
 fields: ldata,
 prefix: "threat_intel_"
 );
end

